package com.aiglepub.composecomponents.examples.modifierimport androidx.compose.foundation.backgroundimport androidx.compose.foundation.borderimport androidx.compose.foundation.clickableimport androidx.compose.foundation.layout.Boximport androidx.compose.foundation.layout.fillMaxSizeimport androidx.compose.foundation.layout.paddingimport androidx.compose.material3.Textimport androidx.compose.runtime.Composableimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.tooling.preview.Previewimport androidx.compose.ui.unit.dp/** * MODIFIER * Para modificar propiedades de los composables * * - De Posicionamiento --> posicion y tamaÃ±os: fillMaxSize, width, height.... * - De funcionalidad --> ampliar caracteristicas: clickable, enabled,... * - De apariencia --> cambiar estlos: padding, background,... * - De escucha --> para estar escuchando: listeners, onclickChange,... * * Intentamos pasar el modifier en el constructor para poder ir pasando de * Composables */@Composablefun MyCustomTextToButton(modifier: Modifier = Modifier) {    Box(        modifier = modifier.fillMaxSize(),        contentAlignment = Alignment.Center    ) {        Text(            text = "Hello WORLD",            modifier = Modifier                .clickable { /*TODO()*/ }                .background(Color.Cyan)                .border(width = 2.dp, color = Color.Black)                .padding(horizontal = 16.dp, vertical = 8.dp)        )    }}@Preview(showBackground = true, widthDp = 200, heightDp = 100)@Composableprivate fun MyCustomTextToButton_Preview() {    MyCustomTextToButton()}